import React from 'react';
import { Descriptions, Image, Modal, ModalProps, Spin, Statistic } from 'antd';
import { Phone } from 'src/features/phones/phonesApi';
import styled from 'styled-components';

const GalleryContainer = styled.article`
  display: flex;
  flex-direction: row;
`;

interface GalleryDetailModalProps extends ModalProps {
  galleryItem?: Phone;
}

const GalleryDetailModal: React.FC<GalleryDetailModalProps> = ({
  galleryItem,
  visible,
  onCancel,
}) => {
  return (
    <Modal
      destroyOnClose
      onCancel={onCancel}
      visible={visible}
      footer={null}
      closable
      width={800}
    >
      <GalleryContainer>
        <Image
          src={galleryItem?.imageFileName}
          placeholder={<Spin size="large" />}
          fallback="data:image/png;base64,"
        />
          <Descriptions title={galleryItem?.name} bordered column={{ xxl: 1, xl: 1, lg: 1, md: 1, sm: 1, xs: 1}}>
            <Descriptions.Item label="Manufacturer">{galleryItem?.manufacturer}</Descriptions.Item>
            <Descriptions.Item label="Description">{galleryItem?.description}</Descriptions.Item>
            <Descriptions.Item label="Color">{galleryItem?.color}</Descriptions.Item>
            <Descriptions.Item label="Screen">{galleryItem?.screen}</Descriptions.Item>
            <Descriptions.Item label="Processor">{galleryItem?.processor}</Descriptions.Item>
            <Descriptions.Item label="ram">{`${galleryItem?.ram}GB`}</Descriptions.Item>
            <Descriptions.Item label="Price">
              <Statistic value={galleryItem?.price} />
            </Descriptions.Item>
          </Descriptions>
      </GalleryContainer>
    </Modal>
  )
};

export default GalleryDetailModal;